import { useState, useEffect } from "react";
import { ListGroup, Pagination } from "react-bootstrap";

function App() {
  const [people, setPeople] = useState([]);
  const [total, setTotal] = useState(0);
  const [url, setUrl] = useState("https://www.swapi.tech/api/people/");

  let active = 1;
  let items = [];
  for (let number = 1; number <= total; number++) {
    items.push(
      <Pagination.Item key={number} href={``} active={number === active}>
        {number}
      </Pagination.Item>
    );
  }

  useEffect(() => {
    async function fetchData() {
      const resp = await fetch(url);

      const data = await resp.json();

      console.log(data);

      const { results } = data;
      setPeople(results);
      setTotal(data.total_pages);
    }

    fetchData();

    return () => {};
  }, [url]);

  return (
    <div>
      <h1>SWAPI</h1>

      <ListGroup>
        {people &&
          people.map((person) => {
            return (
              <ListGroup.Item
                action
                href={`/people/${person.uid}`}
                key={person.uid}
              >
                {person.name}
              </ListGroup.Item>
            );
          })}
      </ListGroup>

      <Pagination size="sm">
        <Pagination.First />
        <Pagination.Prev />
        <Pagination.Next />
        <Pagination.Last />
      </Pagination>
    </div>
  );
}

export default App;
