import { useState, useEffect } from "react";

function App() {
  const [people, setPeople] = useState([]);

  useEffect(async () => {
    async function fetchData() {
      const resp = await fetch("https://www.swapi.tech/api/people/");

      const data = await resp.json();

      const { results } = data;
      setPeople(data);
    }
    fetchData();
  }, []);

  return (
    <div>
      <h1>SWAPI</h1>

      {people &&
        people.map((person) => {
          return <p>{person.name}</p>;
        })}
    </div>
  );
}

export default App;
